import React, { Component } from 'react'
import { connect } from 'react-redux'

class DailyIntake extends Component {
 state = {
     userIntake: []
 }

 componentDidMount(){
   this.setState({
    userIntake: this.props.current_user.daily_intakes
   })
 }


 render() {
     console.log(this.props.current_user.daily_intakes)
     console.log(this.state.userIntake)
    //  console.log(this.state.breakfast)

    const breakfast = () => {
        if(this.props.current_user.id && this.props.current_user.daily_intakes !== undefined) {
            let arr = this.state.userIntake.filter(dailyIntake => {
                return dailyIntake.meal_type === 'breakfast'
            }) 
            return arr.map(dailyIntake => {
                return  <tr><td key={dailyIntake.food.id}> {dailyIntake.food.name }</td></tr>
             })
           }
           return "nope!"
        }

    const lunch = () => {
        if(this.props.current_user.id && this.state.userIntake !== undefined) {
            let arr = this.state.userIntake.filter(dailyIntake => {
                return dailyIntake.meal_type === 'lunch'
            }) 
            return arr.map(dailyIntake => {
                return  <tr><td key={dailyIntake.food.id}> {dailyIntake.food.name }</td></tr>
             })
           }
           return "nope!"
        }

    const dinner = () => {
        if(this.props.current_user.id && this.state.userIntake !== undefined) {
            let arr = this.state.userIntake.filter(dailyIntake => {
                return dailyIntake.meal_type === 'dinner'
            }) 
            return arr.map(dailyIntake => {
                return  <tr><td key={dailyIntake.food.id}> {dailyIntake.food.name }</td></tr>
             })
           }
           return "nope!"
        }

    const snacks = () => {
        if(this.props.current_user.id && this.state.userIntake !== undefined) {
            let arr = this.state.userIntake.filter(dailyIntake => {
                return dailyIntake.meal_type === 'snack'
            }) 
            return arr.map(dailyIntake => {
                return  <tr><td key={dailyIntake.food.id}> {dailyIntake.food.name }</td></tr>
             })
           }
           return "nope!"
        }



  return(
<div>

<table>
    <thead>
     <tr>
        <th width="10%"></th> 
        <th width="10%">Calorie</th> 

        <th width="10%">Fat</th>
        <th width="10%">Carbs</th>
        <th width="10%">Protein</th>
        <th width="10%">Sugar</th>
     </tr>
    </thead>
    <tbody>

    <tr>
      <th width="10%">Breakfast</th>
    </tr> 
      {breakfast()}
    <tr>
      <th width="10%">Lunch</th> 
    </tr>
     {lunch()}
    <tr>
      <th width="10%">Dinner</th> 
    </tr>
     {dinner()}
    <tr>
      <th width="10%">Snack</th> 
    </tr>
      {snacks()} 
    </tbody>
 </table>
 </div>
    )
   }
 }
 const mapStateToProps = (state) => {
    return {
      current_user: state.user
    }
  }


 export default connect(mapStateToProps)(DailyIntake)
