import React, { Component } from 'react'
import { connect } from 'react-redux'

class DailyIntake extends Component {
 state = {
     breakfast: {},
     lunch: {},
     dinner: {},
     snacks:{}
 }

  mealType = ()=>{
    const {daily_intakes} =  this.props.current_user
    if (daily_intakes.length > 0){
        daily_intakes.map((food) =>{
            if(food.meal_type === 'breakfast'){
               return this.setState({breakfast: food})
            } else if (food.meal_type === 'lunch') {
                return this.setState({lunch: food})
            } else if (food.meal_type === 'dinner'){
               return this.setState({dinner: food})
            } else {
               return this.setState({snacks: food})
            }
        })
    }

 }
