import React, { Component } from 'react'



class Login extends Component {
 state = {
     username: '',
     password: '',
     name: '',
     age: null,
     gender: "",
     height: null,
     weight: null,
     goalCalorie: null,
     errors: '', 
     loginOn: true
 }

loginForm = () => {
  return (<form onSubmit={this.handleLoginSubmit}>
           <label htmlFor="username">
              Username :
             <input type="text"
                  id="username"
                  value={this.state.username}
                  name="username"
                  onChange={this.handleChange}
             />
            </label>
            <label htmlFor="password">
              Password :
              <input type="password"
                    id="password"
                    value={this.state.password}
                    name="password"
                    onChange={this.handleChange}
              />
            </label>
            <input type="submit"/>
          </form>)
}

