  state = {
    pages: 'login'
  }

  redirect = (pages) =>{
    this.setState({
      pages: pages
    })
  }

  componentDidMount(){
    console.log(localStorage.token)
    if (localStorage.token){
      this.redirect('home')
    } else {
      this.redirect('login')
    }
  }

