import React, { Component } from 'react'
import { connect } from 'react-redux'
import EditDailyIntake from '../Components/EditDailyIntake'

class DailyIntake extends Component {
 state = {
   editForm: false, 
   dailyIntake: {},
   serving: null,
   mealType: "", 
   dailyIntakes: [],
   total_calories: 0,
   total_protein: 0,
   total_sugar: 0,
   total_fat: 0,
   total_servings: 0,
   total_carbs: 0
 }


