import React from 'react';
import './App.css';
import Pages from ""


class Routes extends React.Component  {

  state = {
    pages: 'login'
  }

  redirect = (pages) =>{
    this.setState({
      pages: pages
    })
  }

  componentDidMount(){
    console.log(localStorage.token)
    if (localStorage.token){
      this.redirect('home')
    } else {
      this.redirect('login')
    }
  }


 render(){



       switch(this.state.pages){
         case "login":
           return <Pages.Login redirect={this.redirect}/>
         case 'home':
           return <Pages.Home/>
       }

 }
}

export default App;
