import React, { Component } from 'react'
// import {Link} from 'react-router-dom'

class FoodForm extends Component {


    state={
        serving: null,
        mealType: ""
    }

    handleChange = (e) => {
        // console.log('handle change', e.target.value)
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault()
        console.log('submit')
        fetch("http://localhost:3000/meal_types", {
            method: 'POST',
            headers: {
                'Content-type':'application/json',
                'Accept':'application/json'
            },
            body: JSON.stringify({
                meal_type: this.state.mealType
            })
        })
        .the
    }
    // 
    render() {

        console.log(this.state)
        const {name, calorie, carb, sugar, fat, serving_size, protein} = this.props.food
        return(
            <div> 
                Food Form


                     <h3>{ name }</h3>
                     <form onSubmit={this.handleSubmit}>
                         <label htmlFor="serving"> Servings of </label> 
                            <input 
                                id="serving"
                                name="serving"
                                type="number"
                                onChange={this.handleChange}
                            />
                            <input
                                type="hidden" 
                                id="user"
                                name="userId"
                            />
                            <input
                                type="hidden" 
                                id="food"
                                name="foodId"
                            />
                            <input
                                type="hidden" 
                                id="mealType"
                                name="mealType"
                            />
                         <label htmlFor="mealType"> Meal Type </label>
                           <select id="mealType" name="mealType" onChange={this.handleChange}>
                                <option name=""></option>
                                <option value="breakfast">Breakfast</option>
                                <option value="lunch">Lunch</option>
                                <option value="dinner">Dinner</option>
                                <option value="snack">Snack</option>
                           </select>
                           <input type="submit" value="Add to your dairy!" />
                     </form>
                    <button onClick={this.props.handleClick}> ‚ùå</button>


            </div>
           )
    }


 }


export default FoodForm
