     foodForm: false,
     food: {}
 }

 handleChange = (e) => {
     console.log(e.target.value)
     this.setState({
         searchValue: e.target.value

     })

 }

 handleClick = (foodItem) => {
     console.log('click')
     this.setState({
         foodForm: !this.state.foodForm,
         food: foodItem
     })
 }

 componentDidMount(){
    fetch('http://localhost:3000/foods')
    .then(res=>res.json())
    .then(foodsData => {
        // console.log(foodsData)
        this.props.getFoods(foodsData)
    })
}


