import React, { Component } from 'react'
import { connect } from 'react-redux'

class Edit extends Component {

 state = {
    username: "",
    name: '',
    age: null,
    gender: "",
    height: null,
    weight: null,
    goalCalorie: null,
    errors: ''
 }

 handleChange = (event) => {
    console.log(event.target.value)
    this.setState({
      [event.target.name] : event.target.value
    })
  }

  handleLoginSubmit = (event) => {

    event.preventDefault()
   //  debugger
    fetch(`http://localhost:3000/user/${this.props.current_user_user.id}`, {
      method: 'POST',
      headers: {
        "Content-Type" : "application/json",
        "Accept" : "application/json"
      }, 
      body: JSON.stringify({
        username: this.state.username,
        password: this.state.password
      })
    })
    .then(res => res.json())
    .then(userData =>{
      localStorage.setItem('token', userData.token)
      console.log(userData)
       if(userData.token){
         this.props.setUser(userData.current_user)
         this.props.history.push(`/profile`)

       }  else {
         this.setState({
           errors: userData.error
         })
       }
    })
    .then(
      this.setState({
       username: "",
       password: ''
      })
    )
 }
 render() {
     console.log(this.props.current_user)
  return(
   <div>
       Edit Form
    <form onSubmit={this.handleSignupSubmit}>
    <label htmlFor="name">
      Name :
       <input 
         id="name"
         name="name"
         value={this.state.name}
         type="text"
         onChange={this.handleChange}
       />
    </label>
    <label htmlFor="username">
      Username : 
       <input 
         id="username"
         name="username"
         value={this.state.username}
         type="text"
         onChange={this.handleChange}
       />
    </label>
    <label htmlFor="age">
      Age: 
       <input 
         name="age"
         id="age"
         age="age"
         value={this.state.age}
         type="number"
         onChange={this.handleChange}
       />
    </label>
    <label htmlFor="gender">
      Gender :
      <select name="gender" id="gender"
      onChange={this.handleChange}>
        <option> Select</option>
         <option value="Female">Female</option>
         <option value="Male">Male</option>
         <option value="Prefered not to say">Prefered not to say</option>
      </select>
    </label>
    <label htmlFor="weight" >
      Weight : 
       <input 
         id="weight"
         name="weight"
         value={this.state.weight}
         type="number"
         onChange={this.handleChange}
       />
    </label>
    <label htmlFor="height">
      Height : 
       <input 
         id="height"
         name="height"
         value={this.state.height}
         type="float"
         onChange={this.handleChange}
       />
    </label>
    <label htmlFor="goalCalorie" >
      Daily goal calorie : 
       <input 
         id="goalCalorie"
         name="goalCalorie"
         value={this.state.goalCalorie}
         type="number"
         onChange={this.handleChange}
       />
    </label>
    <label htmlFor="password" >
      Password : 
       <input 
         id="password"
         name="password"
         value={this.state.password}
         type="password"
         onChange={this.handleChange}
       />
    </label>
    <input type="submit"/>
    </form>
   </div>
    )
   }
 }



 const mapDispatchToProps = (dispatch) =>{
    return {
      setUser: (userObject) => {
        dispatch({
            type: 'SET_USER', payload: userObject
          })
      }
    }
  }

  const mapStateToProps = (state) => {
    return {
      current_user: state.user
    }
  }


  export default connect(mapStateToProps, mapDispatchToProps) (Edit)
