import React, { Component } from 'react'
import { connect } from 'react-redux'

class AddFood extends Component {
 state = {
     searchValue:"",
     foodView: false
 }

 handleChange = (e) => {
     console.log(e.target.value)
     this.setState({
         searchValue: e.target.value

     })

 }

 handleClick = () => {
     console.log('click')
     this.setState({
         foodView: !this.state.foodView
     })
 }

 componentDidMount(){
    fetch('http://localhost:3000/foods')
    .then(res=>res.json())
    .then(foodsData => {
        // console.log(foodsData)
        this.props.getFoods(foodsData)
    })
}
 render() {

console.log(this.state.foodView)
// console.log(this.state.searchValue)
// console.log(this.props.foods)

   const searchedFoods = this.props.foods.filter(food => {
    //    debugger
         if (this.state.searchValue !== ""){
            return food.name.toLowerCase().includes(this.state.searchValue.toLowerCase())
         } 
   })
  return(
   <div>

       Add food 

       <form>
           <label>
               Search
           </label>
          <input type="text" onChange={this.handleChange}/>

       </form>

       <ul>


            {
                searchedFoods.map(food => {
                    return <li onClick={this.handleClick}>
                        {food.name}
                    </li>
                })
            }

       </ul>

       {
           this.state.foodView 
       }


   </div>
    )
   }
 }

 const mapDispatchToProps = (dispatch) =>{
    return {
      getFoods: (foodObject) => {
        dispatch({
            type: 'GET_FOODS', payload: foodObject
          })
      }
    }
  }

  const mapStateToProps = (state) => {
    return {
     foods: state.foods
    }
  }


  export default connect(mapStateToProps, mapDispatchToProps) (AddFood)
