import React, { Component } from 'react'
import { connect } from 'react-redux'



class Edit extends Component {

 state = {
    name: ''
 }

 componentDidUpdate(){
    this.setState({
        name: this.props.current_user.user.name
    })

    console.log
 }

 handleChange = (event) => {
    console.log(event.target.value)
    this.setState({
      [event.target.name] : event.target.value
    })
  }

  handleLoginSubmit = (event) => {

    event.preventDefault()
   //  debugger
    fetch(`http://localhost:3000/user/${this.props.current_user_user.id}`, {
      method: 'POST',
      headers: {
        "Content-Type" : "application/json",
        "Accept" : "application/json"
      }, 
      body: JSON.stringify({


      })
    })
    .then(res => res.json())
    .then(userData =>{
      localStorage.setItem('token', userData.token)
      console.log(userData)
       if(userData.token){
         this.props.setUser(userData.current_user)
         this.props.history.push(`/profile`)

       }  else {
         this.setState({
           errors: userData.error
         })
       }
    })
    .then(
      this.setState({
       username: "",
       password: ''
      })
    )
 }
 render() {
     console.log(this.state)
     console.log(this.props.current_user.user)
     const {name, username, age, weight, height, goalCalorie} = this.state
  return(
      <>
      {this.props.current_user.user.id  ? 

        <div>
       Edit Form
    <form onSubmit={this.handleSignupSubmit}>
    <label htmlFor="name">
      Name :
       <input 
         id="name"
         name="name"
         value={this.state.name}
         type="text"
         onChange={this.handleChange}
       />
    </label>
    <label htmlFor="username">
      Username : 
       <input 
         id="username"
         name="username"
         value={username}
         type="text"
         onChange={this.handleChange}
       />
    </label>
    <label htmlFor="age">
      Age: 
       <input 
         name="age"
         id="age"
         age="age"
         value={age}
         type="number"
         onChange={this.handleChange}
       />
    </label>
    <label htmlFor="gender">
      Gender :
      <select name="gender" id="gender"
      onChange={this.handleChange}>
        <option> Select</option>
         <option value="Female">Female</option>
         <option value="Male">Male</option>
         <option value="Prefered not to say">Prefered not to say</option>
      </select>
    </label>
    <label htmlFor="weight" >
      Weight : 
       <input 
         id="weight"
         name="weight"
         value={weight}
         type="number"
         onChange={this.handleChange}
       />
    </label>
    <label htmlFor="height">
      Height : 
       <input 
         id="height"
         name="height"
         value={height}
         type="float"
         onChange={this.handleChange}
       />
    </label>
    <label htmlFor="goalCalorie" >
      Daily goal calorie : 
       <input 
         id="goalCalorie"
         name="goalCalorie"
         value={goalCalorie}
         type="number"
         onChange={this.handleChange}
       />
    </label>

    <input type="submit"/>
    </form>
   </div> : null
     }
     </>

    )
   }
 }



 const mapDispatchToProps = (dispatch) =>{
    return {
      setUser: (userObject) => {
        dispatch({
            type: 'SET_USER', payload: userObject
          })
      }
    }
  }

  const mapStateToProps = (state) => {
    return {
      current_user: state.user
    }
  }


  export default connect(mapStateToProps, mapDispatchToProps) (Edit)
