import React, { Component } from 'react'



class Login extends Component {
 state = {
     username: '',
     password: '',
     name: '',
     age: null,
     gender: "",
     height: null,
     weight: null,
     goalCalorie: null,
     errors: '', 
     loginOn: true
 }

loginForm = () => {
  return (<form onSubmit={this.handleLoginSubmit}>
           <label htmlFor="username">
              Username :
             <input type="text"
                  id="username"
                  value={this.state.username}
                  name="username"
                  onChange={this.handleChange}
             />
            </label>
            <label htmlFor="password">
              Password :
              <input type="password"
                    id="password"
                    value={this.state.password}
                    name="password"
                    onChange={this.handleChange}
              />
            </label>
            <input type="submit"/>
          </form>)
}

handleChange = (event) => {
  console.log(event.target.value)
  this.setState({
    [event.target.name] : event.target.value
  })
}

handleClick = ()=>{
  this.setState({
    loginOn: !this.state.loginOn
  })
}

handleLoginSubmit = (event) => {
   event.preventDefault()
   fetch('http://localhost:3000/login', {
     method: 'POST',
     headers: {
       "Content-Type" : "application/json",
       "Accept" : "application/json"
     }, 
     body: JSON.stringify({
       username: this.state.username,
       password: this.state.password
     })
   })
   .then(res => res.json())
   .then(userData =>{
     localStorage.setItem('token', userData.token)
     console.log(userData)
      if(userData.token){
        this.props.redirect('home')
      }  else {
        this.setState({
          errors: userData.error,
          username: "",
          password: ''
        })
      }
   })
}

handleSignupSubmit = (event) => {
  event.preventDefault()
  fetch("http://localhost:3000/signup", {
    method: "POST",
    headers: {
      'Content-Type':"application/json",
      "Accept":"application/json"
    },
    body: JSON.stringify({
      name: this.state.name,
      username: this.state.username,
      age: this.state.age,
      gender: this.state.gender,
      weight: this.state.weight,
      height: this.state.height,
      goalCalorie: this.state.goalCalorie,
      password: this.state.password
    })
  })
  .then(res => res.json())
  .then(userData => {
    console.log(userData)
  })
}

 render() {

  //  console.log(this.state)
  return(
   <div>
    {
      this.state.errors
    }
    <h1> {this.state.loginOn ? "Login Page" : "Sign Up Page" }</h1>

    <p>{this.state.loginOn ? 
      "Don't have an account yet?" : 
      "I have an account" } 
      <b onClick={this.handleClick}>
      { this.state.loginOn ? 
        "Sign Up!" : 
        "Login!" }
        </b>
       </p>
    {
      this.state.loginOn ? 
      this.loginForm()
     :
        <form onSubmit={this.handleSignupSubmit}>
          <label htmlFor="name">
            Name :
             <input 
               id="name"
               name="name"
               value={this.state.name}
               type="text"
               onChange={this.handleChange}
             />
          </label>
          <label htmlFor="username">
            Username : 
             <input 
               id="username"
               name="username"
               value={this.state.username}
               type="text"
               onChange={this.handleChange}
             />
          </label>
          <label htmlFor="age">
            Age: 
             <input 
               id="age"
               age="age"
               value={this.state.age}
               type="number"
               onChange={this.handleChange}
             />
          </label>
          <label htmlFor="gender">
            Gender :
            <select name="gender" id="gender"
            onChange={this.handleChange}>

               <option value="Female">Female</option>
               <option value="Female">Male</option>
               <option value="Prefered not to say">Prefered not to say</option>
            </select>
          </label>
          <label htmlFor="weight" onChange={this.handleChange}>
            Weight : 
             <input 
               id="weight"
               name="weight"
               value={this.state.weight}
               type="number"
             />
          </label>
          <label htmlFor="height" onChange={this.handleChange}>
            Height : 
             <input 
               id="height"
               name="height"
               value={this.state.height}
               type="float"
             />
          </label>
          <label htmlFor="goalCalorie" onChange={this.handleChange}>
            Daily goal calorie : 
             <input 
               id="goalCalorie"
               name="goalCalorie"
               value={this.state.goalCalorie}
               type="number"
             />
          </label>
          <label htmlFor="password" onChange={this.handleChange}>
            Password : 
             <input 
               id="password"
               name="password"
               value={this.state.password}
               type="password"
             />
          </label>
          <input type="submit"/>
          </form>
    }


   </div>
    )
   }
 }


export default Login
