import React, { Component } from 'react'
import { connect } from 'react-redux'
import EditDailyIntake from '../Components/EditDailyIntake'

class DailyIntake extends Component {
 state = {
   editForm: false, 
   dailyIntake: {},
   serving: null,
   mealType: "", 
   dailyIntakes: []
 }


// handleClick = (e, dailyIntakeItem) => {
//   console.log(dailyIntakeItem.id)
//   this.setState({
//     show: false
//   })
//   fetch(`http://localhost:3000/daily_intakes/${dailyIntakeItem.id}`, {
//      method: 'DELETE'
//    })
// }


handleClick = (e, dailyIntakeItem) => {
//  console.log(dailyIntakeItem)
 this.setState({
   editForm: !this.state.editForm,
   dailyIntake: dailyIntakeItem,
   serving: dailyIntakeItem.serving,
   mealType: dailyIntakeItem.meal_type
 })
}

componentDidMount(){
  this.fetchDailyIntake()
}

fetchDailyIntake =  () => {
  const config = {
    method: 'GET',
    headers: {
      Authorization: localStorage.token
    }
  }
  fetch('http://localhost:3000/daily_intakes', config)
  .then(res => res.json())
  .then(dailyIntakeItems => {
    console.log(dailyIntakeItems)

        this.setState({
          dailyIntakes: dailyIntakeItems
        }, () => console.log("state ----- ", this.state))


  })
}

handleChange = (e) =>{
  // console.log(e.target.value)
  this.setState({
    [e.target.name] : e.target.value
  })
}

handleEditSubmit = (e) => {
  e.preventDefault()
  // console.log('submit')
  fetch(`http://localhost:3000/daily_intakes/${this.state.dailyIntake.id}`, {
    method: 'PATCH', 
    headers: {
      'Content-Type': 'application/json',
      'Accept':'application/json'
    },
    body: JSON.stringify({ 
     meal_type: this.state.mealType,
     serving: this.state.serving
    })
  })
  .then(res => res.json())
  .then(intake => {
    const newItem =  this.state.dailyIntakeItems.map(intakeItem => {
        return intakeItem.id === inta
    })
    // this.props.setUser(user) 
    this.setState({
      dailyIntake: intake,
      dailyIntakeItems:  [...this.state.dailyIntakeItems, intake]
    })
  })
} 

 render() {


  // const date = (() => {
