          return  dailyIntakeItem.changed_date === new Date().toLocaleDateString()

        })
        this.setState({
          dailyIntakes: todayIntakes
        })
      }
    }
  })
}

handleChange = (e) =>{
  // console.log(e.target.value)
  this.setState({
    [e.target.name] : e.target.value
  })
}

handleEditSubmit = (e) => {
